<?php

$dbservername = 'localhost';
$dbusername = 'root';
$dbpassword = '';
$dbname = 'fem-vibes';

$db = new PDO("mysql:host=$dbservername;dbname=$dbname", $dbusername, $dbpassword);

$db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);

$sqlQuery = "SELECT author.author_name , author.author_surname, author.author_years, author.author_bio, author.img, quotes.quote, quotes.reference
FROM author INNER JOIN quotes ON quotes.id_author = author.author_id ORDER BY RAND() LIMIT 1";

$authorStatement = $db->prepare($sqlQuery);
$authorStatement->execute();
$citation = $authorStatement->fetch();

$texte = $citation['quote'];
$name = $citation['author_name'];
$surname = $citation['author_surname'];
$reference = $citation['reference'];
$img = $citation['img'];
$dates = $citation['author_years'];
$bio = $citation['author_bio'];

?>

